mapper.select:
select count(*) 
from member_tb
where mb_id = #{mb_id}

mapper.update:
update member_tb
set mb_pw = #{mb_pw}
where mb_id = #{mb_id}

mapper.select:
select mb_id, mb_pw, mb_name, mb_nick, mb_point, mb_lasttime
from member_tb
where mb_id = #{mb_id}

mapper.update:
update member_tb
set mb_lasttime = sysdate
where mb_id = #{mb_id}
